mapper.select:
SELECT obj_new.sale_id, obj_new.phone, obj_new.bind_user_num, obj_new.order_count, obj_new.sale_money, obj_new.rownum FROM ( SELECT obj.sale_id, obj.phone, obj.bind_user_num, obj.order_count, obj.sale_money, @rownum := @rownum + 1 AS num_tmp, @incrnum := CASE WHEN @rowtotal = obj.sale_money THEN @incrnum WHEN @rowtotal := obj.sale_money THEN @rownum END AS rownum FROM ( SELECT s.sale_id,s.phone,s.bind_user_num,COUNT(si.order_id) AS order_count, (IF(ROUND(SUM(si.order_money_haocai),2) IS NOT NULL,ROUND(SUM(si.order_money_haocai),2),0)+IF(ROUND(SUM(si.order_money_gongju),2) IS NOT NULL,ROUND(SUM(si.order_money_gongju),2),0))- (IF(ROUND(SUM(si.refund_money_haocai),2) IS NOT NULL,ROUND(SUM(si.refund_money_haocai),2),0)+IF(ROUND(SUM(si.refund_money_gongju),2) IS NOT NULL,ROUND(SUM(si.refund_money_gongju),2),0)) AS sale_money FROM sale_income si LEFT JOIN sale_info s ON s.sale_id=si.sale_id WHERE DATE_FORMAT(si.created,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate} GROUP BY si.sale_id ORDER BY sale_money DESC
LIMIT 20
) AS obj, (SELECT @rownum := 0 ,@rowtotal := NULL ,@incrnum := 0) r ) AS obj_new

		SELECT sale_num FROM sale_num 
    	WHERE 
    	DATE_FORMAT(created,'%Y-%m')=#{beYearMonth}
    	LIMIT 1;

SELECT phone,bind_user_num,order_count,sale_money,row_num
FROM ${tableName} WHERE phone IN (SELECT phone FROM sale_info WHERE sale_id=#{saleId})
LIMIT 1;
