mapper.select:
		SELECT a.item_name,a.sales,a.item_price,b.item_brand_name,c.item_pica
FROM item_info a,item_brand b,item_detail c
WHERE a.item_brand_id=b.item_brand_id AND a.item_id=c.item_id AND c.video=#{viId}

mapper.update:
update vid_manage


		video_name=#{vidName},


		video_type=#{videoType},


		video_route=#{vidRoute},


		video_category=#{videoCategory}


where vi_id=#{viId}

mapper.select:
select vi_id,video_name,video_type,video_route,video_category,DATE_FORMAT(vedio_time,'%Y-%m-%d %H:%i:%s') As vedio_time,vedio_pic,play_num,comment_num AS vedio_comment_number from vid_manage
video_category=#{videoCategory}
and video_name like '%${keyWord}%'
order by
play_num DESC comment_num DESC vedio_time DESC
limit #{page.currentNumber},#{page.numberPerPage}

select * from vid_manage where vi_id IN 

	#{id}

limit #{currentNumber},#{numberPerPage}

select count(*) FROM vid_manage


		video_category=#{videoCategory}


		and video_name like '%${keyWord}%'
