mapper.select:
select item_brand_id,item_brand_home,item_brand_logo,item_brand_name from item_brand 

 true
 and item_brand_name=#{itemBrandName}
 and item_brand_Home=#{itemBrandHome}

SELECT d.item_id,d.item_pica,d.item_name,c.item_brand_name,d.item_price,d.sales,d.updated,c.item_brand_id FROM (SELECT * FROM (SELECT item_id,item_name,item_brand_id p_id,item_price,sales,updated FROM item_info) a INNER JOIN (SELECT item_id did,item_pica FROM item_detail) b ON a.item_id=b.did) d INNER JOIN (SELECT * FROM item_brand WHERE item_brand_id=#{itemBrandId}) c ON d.p_id=c.item_brand_id order by
updated desc sales desc item_price desc item_price aesc
LIMIT ${currentNumber},${numberPerPage}
