mapper.select:
SELECT s.sale_id,u.user_id,s.true_name AS sale_name,s.phone AS sale_phone,o.order_id,o.state AS order_state, o.created AS order_created,IF(DATEDIFF(NOW(),o.end_time)<7,2,1) AS sign_late_seven,u.user_id, si.get_money,si.get_state,si.updated AS get_updated FROM sale_info s LEFT JOIN USER u ON s.sale_id=u.sale_id LEFT JOIN ordera o ON u.user_id=o.user_id LEFT JOIN sale_income si ON o.order_id=si.order_id
s.sale_id LIKE '%${saleId}%'
AND s.true_name LIKE '%${saleName}%'
AND s.phone LIKE '%${salePhone}%'
AND o.order_id LIKE '%${orderId}%'
AND DATEDIFF(NOW(),o.end_time) >= 7
AND DATEDIFF(NOW(),o.end_time) < 7
AND si.get_state = #{getState}
AND DATE_FORMAT(si.updated,'%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(si.updated,'%Y-%m-%d') <= #{endDate}
GROUP BY o.order_id ORDER BY o.created LIMIT ${page.currentNumber},${page.numberPerPage}
