mapper.select:
SELECT s.sale_id, s.true_name AS saleName, s.phone AS salePhone, IF(b.balance_in IS NOT NULL,b.balance_in,0) AS getMoney, DATE_FORMAT(DATE_SUB(b.created,INTERVAL 1 MONTH),'%Y-%m') AS beYearMonth, "已结算" AS getState, b.created AS settlementTime, IF(ROUND(SUM(b.haocai_refund),2) IS NOT NULL,ROUND(SUM(b.haocai_refund),2),0)+IF(ROUND(SUM(b.gongju_refund),2) IS NOT NULL,ROUND(SUM(b.gongju_refund),2),0) AS moneyRefund, IF(ROUND(SUM(b.haocai_money),2) IS NOT NULL,ROUND(SUM(b.haocai_money),2),0)+IF(ROUND(SUM(b.gongju_money),2) IS NOT NULL,ROUND(SUM(b.gongju_money),2),0) AS allMoney, (IF(ROUND(SUM(b.haocai_money),2) IS NOT NULL,ROUND(SUM(b.haocai_money),2),0)+IF(ROUND(SUM(b.gongju_money),2) IS NOT NULL,ROUND(SUM(b.gongju_money),2),0))- (IF(ROUND(SUM(b.haocai_refund),2) IS NOT NULL,ROUND(SUM(b.haocai_refund),2),0)+IF(ROUND(SUM(b.gongju_refund),2) IS NOT NULL,ROUND(SUM(b.gongju_refund),2),0)) AS allActual FROM balance b LEFT JOIN sale_info s ON s.sale_id=b.sale_id WHERE b.haocai_money IS NOT NULL AND b.gongju_money IS NOT NULL
AND s.true_name LIKE '%${saleName}%'
AND s.phone LIKE '%${salePhone}%'
AND DATE_FORMAT(DATE_SUB(b.created,INTERVAL 1 MONTH),'%Y-%m') = #{beYearMonth}
AND DATE_FORMAT(b.created,'%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(b.created,'%Y-%m-%d') <= #{endDate}
GROUP BY b.balance_id LIMIT ${page.currentNumber},${page.numberPerPage}

SELECT count(0) FROM ( SELECT count(0) FROM balance b LEFT JOIN sale_info s ON s.sale_id=b.sale_id WHERE b.haocai_money IS NOT NULL AND b.gongju_money IS NOT NULL
AND s.true_name LIKE '%${saleName}%'
AND s.phone LIKE '%${salePhone}%'
AND DATE_FORMAT(DATE_SUB(b.created,INTERVAL 1 MONTH),'%Y-%m') = #{beYearMonth}
AND DATE_FORMAT(b.created,'%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(b.created,'%Y-%m-%d') <= #{endDate}
GROUP BY b.balance_id ) AS a

SELECT s.sale_id, s.true_name AS saleName, s.phone AS salePhone, "0" AS getMoney, DATE_FORMAT(si.created,'%Y-%m') AS beYearMonth, "待结算" AS getState, IF(ROUND(SUM(si.order_money_haocai),2) IS NOT NULL,ROUND(SUM(si.order_money_haocai),2),0)+IF(ROUND(SUM(si.order_money_gongju),2) IS NOT NULL,ROUND(SUM(si.order_money_gongju),2),0) AS allMoney, IF(ROUND(SUM(si.refund_money_haocai),2) IS NOT NULL,ROUND(SUM(si.refund_money_haocai),2),0)+IF(ROUND(SUM(si.refund_money_gongju),2) IS NOT NULL,ROUND(SUM(si.refund_money_gongju),2),0) AS moneyRefund, (IF(ROUND(SUM(si.order_money_haocai),2) IS NOT NULL,ROUND(SUM(si.order_money_haocai),2),0)+IF(ROUND(SUM(si.order_money_gongju),2) IS NOT NULL,ROUND(SUM(si.order_money_gongju),2),0))- (IF(ROUND(SUM(si.refund_money_haocai),2) IS NOT NULL,ROUND(SUM(si.refund_money_haocai),2),0)+IF(ROUND(SUM(si.refund_money_gongju),2) IS NOT NULL,ROUND(SUM(si.refund_money_gongju),2),0)) AS allActual FROM sale_income si LEFT JOIN sale_info s ON si.sale_id=s.sale_id WHERE DATE_FORMAT(si.created,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND s.true_name LIKE '%${saleName}%'
AND s.phone LIKE '%${salePhone}%'
GROUP BY si.sale_id LIMIT ${page.currentNumber},${page.numberPerPage}
