mapper.select:
SELECT s.sale_id, s.true_name AS saleName, s.phone AS salePhone, IF(b.balance_in IS NOT NULL,b.balance_in,0) AS getMoney, DATE_FORMAT(b.created,'%Y-%m') AS beYearMonth, "已结算" AS getState, b.created AS settlementTime, SUM(IF(b.haocai_money IS NOT NULL,b.haocai_money,0)+IF(b.gongju_money IS NOT NULL,b.gongju_money,0)) AS allMoney, SUM(IF(b.haocai_money IS NOT NULL,b.haocai_money,0)+IF(b.gongju_money IS NOT NULL,b.gongju_money,0))- SUM(IF(b.haocai_refund IS NOT NULL,b.haocai_refund,0)+IF(b.gongju_refund IS NOT NULL,b.gongju_refund,0)) AS allActual FROM balance b LEFT JOIN sale_info s ON s.sale_id=b.sale_id WHERE b.haocai_money IS NOT NULL AND b.gongju_money IS NOT NULL
AND s.true_name LIKE '%${saleName}%'
AND s.phone LIKE '%${salePhone}%'
AND DATE_FORMAT(b.created,'%Y-%m') = #{beYearMonth}
AND b.created LIKE '%${settlementTime}%'
GROUP BY b.balance_id LIMIT ${page.currentNumber},${page.numberPerPage}

SELECT count(0) FROM ( SELECT count(0) FROM balance b LEFT JOIN sale_info s ON s.sale_id=b.sale_id WHERE b.haocai_money IS NOT NULL AND b.gongju_money IS NOT NULL
AND s.true_name LIKE '%${saleName}%'
AND s.phone LIKE '%${salePhone}%'
AND DATE_FORMAT(b.created,'%Y-%m') = #{beYearMonth}
AND b.created =#{settlementTime}
GROUP BY b.balance_id ) AS a

SELECT s.sale_id, s.true_name AS saleName, s.phone AS salePhone, "0" AS getMoney, DATE_FORMAT(si.created,'%Y-%m') AS beYearMonth, "待结算" AS getState, "NULL" AS settlementTime, SUM(IF(si.order_money_haocai IS NOT NULL,si.order_money_haocai,0)+IF(si.order_money_gongju IS NOT NULL,si.order_money_gongju,0)) AS allMoney, SUM(IF(si.order_money_haocai IS NOT NULL,si.order_money_haocai,0)+IF(si.order_money_gongju IS NOT NULL,si.order_money_gongju,0)) - SUM(IF(si.refund_money_haocai IS NOT NULL,si.refund_money_haocai,0)+IF(si.refund_money_gongju IS NOT NULL,si.refund_money_gongju,0)) AS allActual FROM sale_income si LEFT JOIN sale_info s ON si.sale_id=s.sale_id WHERE DATE_FORMAT(si.created,'%Y-%m-%d') BETWEEN #{startDate} AND #{endDate}
AND s.true_name LIKE '%${saleName}%'
AND s.phone LIKE '%${salePhone}%'
AND DATE_FORMAT(si.created,'%Y-%m') =#{beYearMonth}
GROUP BY si.sale_income_id LIMIT ${page.currentNumber},${page.numberPerPage}

SELECT count(0) FROM ( SELECT count(0) FROM sale_income si LEFT JOIN sale_info s ON si.sale_id=s.sale_id WHERE DATE_FORMAT(si.created,'%m-%d') BETWEEN #{startDate} AND #{endDate}
AND s.true_name LIKE '%${saleName}%'
AND s.phone LIKE '%${salePhone}%'
AND DATE_FORMAT(si.created,'%Y-%m') =#{beYearMonth}
GROUP BY si.sale_income_id ) AS a
