mapper.select:
SELECT c.user_id, u.phone, u.true_name, c.type, c.company_name, c.part, c.work_address, c.state FROM `user` u LEFT JOIN certification c ON c.user_id=u.user_id
u.phone LIKE '%${phone}%'
AND u.true_name LIKE '%${trueName}%'
AND c.company_name LIKE '%${companyName}%'
AND c.type=#{type}
AND c.state=#{state}
AND c.doctor_pic!='' AND c.type!=''
AND c.type!='' AND c.medical_license!='' AND c.business_license!='' AND c.tax_registration!=''
GROUP BY c.user_id ORDER BY c.created DESC LIMIT ${page.currentNumber},${page.numberPerPage}

mapper.update:
UPDATE certification 


		state=#{state},

		fail_reason=#{failReason}

WHERE user_id=#{userId}
