mapper.update:
UPDATE user
true_name=#{trueName}, sex=#{sex}, birthday=#{birthday}, qq=#{qq}, user_pic=#{userPic}
WHERE user_id=#{userId};

UPDATE certification
type=#{type}, company_name=#{companyName}, part=#{part}, work_address=#{workAddress}, doctor_pic=#{doctorPic}, judge=#{judge}, state=1,fail_reason=NULL
WHERE user_id=#{userId};
