mapper.select:
		SELECT q.qb_rid,u.phone,q.qb_rget,q.qb_rout,q.qb_time,q.remark,q.qb_balances
		FROM qb_record q
		LEFT JOIN USER u
		ON u.user_id=q.user_id


				u.phone LIKE '%${phone}%'


				AND DATE_FORMAT(q.qb_time,'%Y-%m-%d')   >=  #{startDate}


				AND DATE_FORMAT(q.qb_time,'%Y-%m-%d')   <=  #{endDate}


		ORDER BY q.qb_time DESC
        LIMIT ${page.currentNumber},${page.numberPerPage}

SELECT COUNT(0) FROM qb_record q LEFT JOIN USER u ON u.user_id=q.user_id
u.phone LIKE '%${phone}%'
AND DATE_FORMAT(q.qb_time,'%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(q.qb_time,'%Y-%m-%d') <= #{endDate}

mapper.update:
UPDATE user 
set
qb_balance=#{qbBalance}
WHERE phone=#{phone};
