mapper.select:
SELECT q.qb_rid,u.phone,q.qb_rget,q.qb_balances,q.qb_rout,DATE_FORMAT(q.qb_time, '%Y-%c-%e %T') AS qb_time,q.remark,u.qb_balance,u.a_qb,u.c_qb FROM qb_record q LEFT JOIN `user` u ON u.user_id=q.user_id
u.phone LIKE '%${phone}%'
AND DATE_FORMAT(q.qb_time,'%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(q.qb_time,'%Y-%m-%d') <= #{endDate}
ORDER BY q.qb_time DESC,q.millisecond DESC LIMIT ${page.currentNumber},${page.numberPerPage}

SELECT COUNT(0) FROM qb_record q LEFT JOIN `user` u ON u.user_id=q.user_id
u.phone LIKE '%${phone}%'
AND DATE_FORMAT(q.qb_time,'%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(q.qb_time,'%Y-%m-%d') <= #{endDate}

select count(1) from qb_record qr join user us on
qr.user_id=us.user_id join charge ch on qr.charge_id=ch.charge_id


		#{userMessage} in (us.true_name,us.phone)


		and qr.remark like '%${qbType}%'


		and qr.referer=#{payType}


		and DATE_FORMAT(qr.created,'%Y-%m-%d')   >=  #{orderCTime}


		and DATE_FORMAT(qr.created,'%Y-%m-%d')   <=  #{orderETime}

select us.true_name,us.phone,qr.qb_rget,ch.money,qr.referer,qr.created
from qb_record qr join user us on
qr.user_id=us.user_id join charge ch
on qr.charge_id=ch.charge_id


		#{userMessage} in (us.true_name,us.phone)


		and qr.remark like '%${qbType}%'


		and qr.referer=#{payType}


		and DATE_FORMAT(qr.created,'%Y-%m-%d')   >=  #{orderCTime}


		and DATE_FORMAT(qr.created,'%Y-%m-%d')   <=  #{orderETime}


		and qr.referer in(2,3);


order by qr.created desc

	limit ${currentNum} , ${numberPerpage}
