mapper.select:
SELECT u.user_id,u.phone,u.true_name,o.updated FROM USER u LEFT JOIN ordera o ON u.user_id=o.user_id
u.phone LIKE '%${phone}%'
AND u.true_name LIKE '%${trueName}%'
AND o.updated BETWEEN #{startDate} AND #{endDate}
GROUP BY o.user_id DESC LIMIT ${page.currentNumber},${page.numberPerPage}

SELECT IF(COUNT(user_id) IS NOT NULL,COUNT(user_id),0) AS orderaCount, IF(SUM(actual_pay) IS NOT NULL,SUM(actual_pay),0) AS orderaMoneyCount, updated AS latelyOrderDate FROM ordera WHERE user_id=#{userId}
AND updated BETWEEN #{startDate} AND #{endDate}
ORDER BY updated DESC LIMIT 1
